"use strict";(()=>{const e=document.querySelector(".calculator"),t=e.querySelector("#amount"),r=e.querySelector("#amount-text"),n=e.querySelector("#term"),a=e.querySelector("#term-text"),l=e.querySelector("#monthly_payment");e.addEventListener("input",e=>{if(e.target===t){let e=t.value.replace(/(\d)(?=(\d\d\d)+([^\d]|$))/g,"$1 ");r.textContent=`${e} ₽`}else a.textContent=`${n.value} мес.`}),e.addEventListener("change",()=>{let e=parseInt(n.value,10),r=parseInt(t.value,10);let a=Math.round(r*(10.99/12/100+10.99/12/100/(Math.pow(10.99/12/100+1,e)-1))).toString().replace(/(\d)(?=(\d\d\d)+([^\d]|$))/g,"$1 ");l.textContent=`${a} ₽`})})();